nne="'.htmlspecialchars ( $_POST['colonne'] ).'"' );			$result_blocs = $Bdd->get_object($req_blocs) ;			$id_down = $result_blocs->id ;			$Bdd->sql('UPDATE '.PT.'_blocs SET position="'.$pos_down.'" WHERE id="'.$id_up.'"') ;			$Bdd->sql('UPDATE '.PT.'_blocs SET position="'.$pos_up.'" WHERE id="'.$id_down.'"') ;		}		}	else if ( isset ( $_GET['colonne'] ) ){		if ( $_POST['type'] == 'right' ){			// On envoie a gauche ^^			$id = intval($_POST['idbloc']);			// On commence par chercher position a appliquer dans lotre colonne			$query = $Bdd->sql ( 'SELECT MAX(position) AS position FROM '.PT.'_blocs WHERE colonne="left"' );			$sql = $Bdd->get_array ( $query );			$position = $sql['position']+1;			// On met a jour le bloc en le virant de lotre cote ^^			$query = $Bdd->sql ( 'SELECT position FROM '.PT.'_blocs WHERE id="'.$id.'"' );			$sql = $Bdd->get_array ( $query );			$Bdd->sql ( 'UPDATE '.PT.'_blocs SET position=(position-1) WHERE position>'.$sql['position'].' AND colonne="right"' );			$Bdd->sql ( 'UPDATE '.PT.'_blocs SET position="'.$position.'",colonne="left" WHERE id="'.$id.'"' );		}		else{			// On envoie a droite ^^			$id = intval($_POST['idbloc']);			// On commence par chercher position a appliquer dans lotre colonne			$query = $Bdd->sql ( 'SELECT MAX(position) AS position FROM '.PT.'_blocs WHERE colonne="right"' );			$sql = $Bdd->get_array ( $query );			$position = $sql['position']+1;			// On met a jour le bloc en le virant de lotre cote ^^			$query = $Bdd->sql ( 'SELECT position FROM '.PT.'_blocs WHERE id="'.$id.'"' );			$sql = $Bdd->get_array ( $query );			$Bdd->sql ( 'UPDATE '.PT.'_blocs SET position=(position-1) WHERE position>'.$sql['position'].' AND colonne="left"' );			$Bdd->sql ( 'UPDATE '.PT.'_blocs SET position="'.$position.'",colonne="right" WHERE id="'.$id.'"' );		}	}	else{			if($_POST['type']==1){			$Bdd->sql('UPDATE '.PT.'_blocs SET afficher="1" WHERE id="'.intval($_POST['idbloc']).'"') ;		}		else{			$Bdd->sql('